    output [7:0] D;
    output B_out;

    wire [7:0] B_neg;
    wire [7:0] B_carry;

    assign B_neg = ~B + 1;
    assign B_carry = B_neg[7];

    full_adder_8bit fa0(A[0], B[0], B_carry, D[0], B_out);
    full_adder_8bit fa1(A[1], B[1], B_carry, D[1], B_out);
    full_adder_8bit fa2(A[2], B[2], B_carry, D[2], B_out);
    full_adder_8bit fa3(A[3], B[3], B_carry, D[3], B_out);
    full_adder_8bit fa4(A[4], B[4], B_carry, D[4], B_out);
    full_adder_8bit fa5(A[5], B[5], B_carry, D[5], B_out);
    full_adder_8bit fa6(A[6], B[6], B_carry, D[6], B_out);
