endmodule

module full_adder_8bit(A, B, B_carry, D, B_out);
    input A, B;
    input B_carry;
    output D;
    output B_out;

    wire [7:0] sum;

    assign sum = A ^ B ^ B_carry;
    assign D = sum ^ B_carry;
    assign B_out = (A & B) | (B_carry & (A ^ B));

endmodule

