module sub_8bit(A, B, D, B_out);
    input [7:0] A, B;
    output [7:0] D;
    output B_out;

    wire [7:0] A_neg, B_neg;
    wire [7:0] D_temp;
    wire B_out_temp;

    assign A_neg = ~A + 1;
    assign B_neg = ~B + 1;

    full_adder fa0(A[0], B[0], 1'b0, D_temp[0], B_out_temp);
    full_adder fa1(A[1], B[1], B_out_temp, D_temp[1], B_out_temp);
    full_adder fa2(A[2], B[2], B_out_temp, D_temp[2], B_out_temp);
    full_adder fa3(A[3], B[3], B_out_temp, D_temp[3], B_out_temp);
    full_adder fa4(A[4], B[4], B_out_temp, D_temp[4], B_out_temp);
    full_adder fa5(A[5], B[5], B_out_temp, D_temp[5], B_out_temp);
    full_adder fa6(A[6], B[6], B_out_temp, D_temp[6], B_out_temp);
    full_adder fa7(A[7], B[7], B_out_temp, D_temp[7], B_out_temp);

    assign D = A_neg ^ B_neg ^ D_temp;
    assign B_out = B_out_temp;

endmodule

