  input rst,
  input [15:0] a,
  input [15:0] b,
  output [31:0] c
);
  reg [31:0] product;
  reg [31:0] accumulator;

  always @(posedge clk or posedge rst) begin
    if (rst) begin
      product <= 32'b0;
      accumulator <= 32'b0;
