module stagepipe5(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  input [31:0] reg_file [0:31],
  output [31:0] res_reg_file [0:31]
);
  wire [31:0] alu_result;
  wire [31:0] pc;
  wire [31:0] next_pc;
  wire [31:0] instr;
  wire [4:0] op;
  wire [4:0] rs1;
  wire [4:0] rs2;
  wire [4:0] rd;
  wire [31:0] rs1_val;
  wire [31:0] rs2_val;
  wire [31:0] rd_val;

  // Fetch stage
  fetch_stage fetch (
    .clk(clk),
    .rst(rst),
    .instr_mem(instr_mem),
    .pc(pc),
    .instr(instr)
  );

  // Decode stage
  decode_stage decode (
    .clk(clk),
    .rst(rst),
    .instr(instr),
    .op(op),
    .rs1(rs1),
    .rs2(rs2),
    .rd(rd)
  );

  // Execute stage
  execute_stage execute (
    .clk(clk),
    .rst(rst),
    .op(op),
    .rs1_val(rs1_val),
    .rs2_val(rs2_val),
    .rd_val(rd_val),
    .alu_result(alu_result)
  );

  // Memory stage
  memory_stage memory (
    .clk(clk),
    .rst(rst),
    .alu_result(alu_result),
    .rd_val(rd_val),
    .res_reg_file(res_reg_file)
  );

  // Writeback stage
  writeback_stage writeback (
    .clk(clk),
    .rst(rst),
    .rd_val(rd_val),
    .res_reg_file(res_reg_file)
  );

  // Pipeline register
  reg [31:0] pipeline_reg [0:4];

  // Assigning pipeline registers
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      pipeline_reg[0] <= 32'b0;
      pipeline_reg[1] <= 32'b0;
      pipeline_reg[2] <= 32'b0;
      pipeline_reg[3] <= 32'b0;
      pipeline_reg[4] <= 32'b0;
    end else begin
      pipeline_reg[0] <= instr;
      pipeline_reg[1] <= op;
      pipeline_reg[2] <= rs1;
      pipeline_reg[3] <= rs2;
      pipeline_reg[4] <= rd;
    end
  end

  // Assigning outputs
  assign instr = pipeline_reg[0];
  assign op = pipeline_reg[1];
  assign rs1 = pipeline_reg[2];
  assign rs2 = pipeline_reg[3];
  assign rd = pipeline_reg[4];
  assign rs1_val = reg_file[rs1];
  assign rs2_val = reg_file[rs2];
  assign rd_val = alu_result;
  assign next_pc = pc + 1;
  assign pc = next_pc;

endmodule

