  input [31:0] instr_mem [0:31],
  input [31:0] reg_file [0:31],
  output [31:0] res_reg_file [0:31]
);

  wire [31:0] fetched_instr;
  wire [31:0] decoded_instr;
  wire [31:0] executed_result;
  wire [31:0] memory_result;

  // Instantiating the pipeline stages
  fetch_stage fetch (
    .clk(clk),
    .rst(rst),
