    .decoded_instr(decoded_instr)
  );

  execute_stage execute (
    .clk(clk),
    .decoded_instr(decoded_instr),
    .executed_result(executed_result)
  );

  memory_stage memory (
    .clk(clk),
    .executed_result(executed_result),
    .memory_result(memory_result)
  );

  writeback_stage writeback (
    .clk(clk),
    .memory_result(memory_result),
    .res_reg_file(res_reg_file)
  );

endmodule

// Fetch stage
module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] fetched_instr
);
  reg [4:0] pc;

  always @(posedge clk or posedge rst) begin
    if (rst) begin
      pc <= 0;
      fetched_instr <= 0;
    end else begin
      fetched_instr <= instr_mem[pc];
      pc <= pc + 1;
    end
  end
endmodule

// Decode stage
module decode_stage(
  input clk,
  input [31:0] fetched_instr,
  output reg [31:0] decoded_instr
);
  always @(posedge clk) begin
    decoded_instr <= fetched_instr;
  end
endmodule

// Execute stage
module execute_stage(
  input clk,
  input [31:0] decoded_instr,
  output reg [31:0] executed_result
);
  reg [4:0] rs1, rs2;
  reg [5:0] op;

  always @(posedge clk) begin
    rs1 <= decoded_instr[25:20];
    rs2 <= decoded_instr[15:10];
    op <= decoded_instr[31:26];
    if (op == 6'b000000) // Addition
      executed_result <= rs1 + rs2;
    else if (op == 6'b001000) // Subtraction
      executed_result <= rs1 - rs2;
    else
      executed_result <= 0;
  end
endmodule

// Memory stage
module memory_stage(
  input clk,
  input [31:0] executed_result,
  output reg [31:0] memory_result
);
  always @(posedge clk) begin
    memory_result <= executed_result;
  end
endmodule

// Writeback stage
module writeback_stage(
  input clk,
  input [31:0] memory_result,
  output reg [31:0] res_reg_file [0:31]
);
  always @(posedge clk) begin
    res_reg_file <= memory_result;
  end
endmodule

