module instr_reg (
    input clk,
    input rst,
    input [1:0] fetch,
    input [7:0] data,
    output [2:0] ins,
    output [4:0] ad1,
    output [7:0] ad2
);

    wire [7:0] ins_p1;
    wire [7:0] ins_p2;

    // Instruction register from RAM/ROM
    reg [7:0] ins_reg1;
    reg [7:0] ins_reg2;

    // Instruction register from register
    reg [7:0] ins_reg3;

    // Instruction register from RAM/ROM
    always @(posedge clk) begin
        if (rst == 0) begin
            ins_reg1 <= 0;
            ins_reg2 <= 0;
            ins_reg3 <= 0;
        end else begin
            if (fetch == 2'b01) begin
                ins_reg1 <= data;
            end else if (fetch == 2'b10) begin
                ins_reg2 <= data;
            end
        end
    end

    // Instruction register from register
    always @(*) begin
        ins_p1 = ins_reg1;
        ins_p2 = ins_reg2;
    end

    assign ins = {ins_p1[7:5], ins_p2[7:5]};
    assign ad1 = {ins_p1[4:0], ins_p2[4:0]};
    assign ad2 = {ins_p1[7:5], ins_p2[7:5]};

endmodule

