    wire [15:0] A_high_low, A_low_low, B_high_low, B_low_low;
    wire [7:0] A_high_high, A_low_high, B_high_high, B_low_high;

    // Instantiate the 32-bit subtractor modules
    sub_32bit sub0(.A(A[31:0]), .B(B[31:0]), .D(D[31:0]), .B_out(B_out));
    sub_32bit sub1(.A(A[63:32]), .B(B[63:32]), .D(D[63:32]), .B_out(B_out));

endmodule

module sub_32bit(A, B, D, B_out);
    input [31:0] A, B;
    output [31:0] D;
    output B_out;

    wire [15:0] A_high, A_low, B_high, B_low;
    wire [7:0] A_high_low, A_low_low, B_high_low, B_low_low;
    wire [3:0] A_high_high, A_low_high, B_high_high, B_low_high;

    // Instantiate the 16-bit subtractor modules
    sub_16bit sub0(.A(A[15:0]), .B(B[15:0]), .D(D[15:0]), .B_out(B_out));
    sub_16bit sub1(.A(A[31:16]), .B(B[31:16]), .D(D[31:16]), .B_out(B_out));

endmodule

module sub_16bit(A, B, D, B_out);
    input [15:0] A, B;
    output [15:0] D;
    output B_out;

    wire [7:0] A_high, A_low, B_high, B_low;
    wire [3:0] A_high_high, A_low_high, B_high_high, B_low_high;

    // Instantiate the 8-bit subtractor modules
    sub_8bit sub0(.A(A[7:0]), .B(B[7:0]), .D(D[7:0]), .B_out(B_out));
    sub_8bit sub1(.A(A[15:8]), .B(B[15:8]), .D(D[15:8]), .B_out(B_out));

endmodule

module sub_8bit(A, B, D, B_out);
    input [7:0] A, B;
    output [7:0] D;
    output B_out;

    wire [3:0] A_high, A_low, B_high, B_low;

    // Perform the subtraction
    assign D = A - B;

    // Calculate the borrow out
    assign B_out = (A[7] & ~B[7]) | (A[7] & B[7] & ~(A[6] ^ B[6]));

endmodule

