module stagepipe3(
    input clk,
    input rst,
    input [31:0] instr_mem [0:31],
    input [31:0] reg_file [0:31],
    output [31:0] out_reg_file  
);

    wire [31:0] fetched_instr;
    wire [31:0] fetched_reg;
    wire [31:0] result;

    // Fetch stage
    fetch_stage fetch (
        .clk(clk),
        .rst(rst),
        .instr_mem(instr_mem),
        .pc(32'd0),
        .fetched_instr(fetched_instr),
        .fetched_reg(fetched_reg)
    );

    // Execute stage
    execute execute (
        .clk(clk),
        .rst(rst),
        .fetched_instr(fetched_instr),
        .fetched_reg(fetched_reg),
        .result(result)
    );

    // Writeback stage
    writeback writeback (
        .clk(clk),
        .rst(rst),
        .result(result),
        .out_reg_file(out_reg_file)
    );

endmodule

// Fetch stage
module fetch_stage(
    input clk,
    input rst,
    input [31:0] instr_mem [0:31],
    input [31:0] pc,
    output [31:0] fetched_instr,
    output [31:0] fetched_reg
);
    assign fetched_instr = instr_mem[pc];
    assign fetched_reg = reg_file[fetched_instr[25:21]];
endmodule

// Execute stage
module execute(
    input clk,
    input rst,
    input [31:0] fetched_instr,
    input [31:0] fetched_reg,
    output [31:0] result
);
    wire [31:0] op1, op2;
    wire [5:0] funct;

    assign op1 = fetched_reg;
    assign op2 = fetched_instr[25:21];
    assign funct = fetched_instr[5:0];

    // Decoder
    always @(*) begin
        case (funct)
            6'b000000: result = op1 + op2; // Add
            6'b000010: result = op1 - op2; // Subtract
            default: result = 32'd0;
        endcase
    end
endmodule

// Writeback stage
module writeback(
    input clk,
    input rst,
    input [31:0] result,
    output reg [31:0] out_reg_file
);
    always @(posedge clk) begin
        if (rst) begin
            out_reg_file <= 32'd0;
        end else begin
            out_reg_file[result[25:21]] <= result;
        end
    end
endmodule

