module execute(
    input clk,
    input rst,
    input [31:0] fetched_instr,
    output reg [31:0] op1,
    output reg [31:0] op2,
    output reg [31:0] result
);

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            op1 <= 0;
            op2 <= 0;
            result <= 0;
        end else begin
            case (fetched_instr[31:28])
                4'b0000: begin // ADD
                    op1 <= fetched_instr[27:0];
                    op2 <= fetched_instr[31:28];
                    result <= op1 + op2;
                end
                4'b0001: begin // SUB
                    op1 <= fetched_instr[27:0];
                    op2 <= fetched_instr[31:28];
                    result <= op1 - op2;
                end
                default: begin
                    op1 <= 0;
                    op2 <= 0;
                    result <= 0;
                end
            endcase
        end
    end

endmodule

