            case (state)
                IDLE: begin
                    if (wave_reg == 5'b000000) begin
                        state <= INC;
                    end
                end
                INC: begin
                    if (wave_reg == 5'b00001) begin
                        state <= DEC;
                    end
                end
                DEC: begin
                    if (wave_reg == 5'b00000) begin
                        state <= IDLE;
                    end
                end
            endcase
        end
    end

    // Waveform generation logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave_reg <= 5'b00000;
        end else begin
            case (state)
                IDLE: begin
                    wave_reg <= 5'b00000;
                end
                INC: begin
                    wave_reg <= wave_reg + 1;
                end
                DEC: begin
                    wave_reg <= wave_reg - 1;
                end
            endcase
        end
    end

