module sub_16bit(A, B, D, B_out);
    input [15:0] A, B;
    output [15:0] D;
    output B_out;

    wire [3:0] B_out_sub1, B_out_sub2, B_out_sub3, B_out_sub4;

    // Instantiate the 4-bit full subtractor blocks
    sub_4bit sub1 (
        .A(A[3:0]),
        .B(B[3:0]),
        .Cin(1'b0),
        .D(D[3:0]),
        .B_out(B_out_sub1)
    );

    sub_4bit sub2 (
        .A(A[7:4]),
        .B(B[7:4]),
        .Cin(B_out_sub1),
        .D(D[7:4]),
        .B_out(B_out_sub2)
    );

    sub_4bit sub3 (
        .A(A[11:8]),
        .B(B[11:8]),
        .Cin(B_out_sub2),
        .D(D[11:8]),
        .B_out(B_out_sub3)
    );

    sub_4bit sub4 (
        .A(A[15:12]),
        .B(B[15:12]),
        .Cin(B_out_sub3),
        .D(D[15:12]),
        .B_out(B_out_sub4)
    );

    // Assign the borrow-out to the output
    assign B_out = B_out_sub4;

endmodule

module sub_4bit(A, B, Cin, D, B_out);
    input [3:0] A, B;
    input Cin;
    output [3:0] D;
    output B_out;

    wire [3:0] G, P;

    assign G = A & B;
    assign P = A ^ B;
    assign D = P ^ Cin;
    assign B_out = G[0] | (P[0] & Cin);
endmodule

