    sub_4bit sub1(.A(A[3:0]), .B(B[3:0]), .D(D[3:0]), .borrow(borrow[3:0]));
    sub_4bit sub2(.A(A[7:4]), .B(B[7:4]), .D(D[7:4]), .borrow(borrow[7:4]));
    sub_4bit sub3(.A(A[11:8]), .B(B[11:8]), .D(D[11:8]), .borrow(borrow[11:8]));
    sub_4bit sub4(.A(A[15:12]), .B(B[15:12]), .D(D[15:12]), .borrow(borrow[15:12]));

