module sub_4bit(A, B, D, borrow_out);
    input [3:1] A, B;
    output [3:1] D;
    output borrow_out;

    wire [3:1] borrow;

    // Perform the subtraction
    assign D = A - B;

    // Calculate the borrow-out
    assign borrow_out = (A[3] & ~B[3]) | (A[3] & B[3] & ~D[3]) | (~A[3] & B[3] & D[3]);

endmodule

module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);

    // State register and state encoding
    parameter IDLE_STATE = 2'b00;
    parameter INC_STATE = 2'b01;
    parameter DEC_STATE = 2'b10;
    parameter STOP_STATE = 2'b11;

    reg [1:0] state;
    reg [4:0] wave_reg;

    // State transition logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            state <= IDLE_STATE;
            wave_reg <= 5'b0;
        end else begin
            case (state)
                IDLE_STATE: begin
                    if (wave_reg == 5'b31) begin
                        state <= INC_STATE;
                    end else begin
                        state <= IDLE_STATE;
                    end
                end
                INC_STATE: begin
                    if (wave_reg == 5'b31) begin
                        state <= DEC_STATE;
                    end else begin
                        state <= INC_STATE;
                    end
                end
                DEC_STATE: begin
                    if (wave_reg == 5'b0) begin
                        state <= IDLE_STATE;
                    end else begin
                        state <= DEC_STATE;
                    end
                end
                STOP_STATE: begin
                    state <= IDLE_STATE;
                end
            endcase
        end
    end

    // Waveform generation logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave_reg <= 5'b0;
        end else begin
            case (state)
                IDLE_STATE: begin
                    wave_reg <= 5'b0;
                end
                INC_STATE: begin
                    wave_reg <= wave_reg + 1;
                end
                DEC_STATE: begin
                    wave_reg <= wave_reg - 1;
                end
                STOP_STATE: begin
                    wave_reg <= 5'b0;
                end
            endcase
        end
    end

    // Output waveform
    assign wave = wave_reg;

endmodule

