    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave_reg <= 5'b00000;
        end else begin
            case (state)
                2'b00: wave_reg <= wave_reg + 5'b00001;
                2'b01: wave_reg <= wave_reg - 5'b00001;
                default: wave_reg <= wave_reg;
            endcase
        end
    end

    // Output waveform
    assign wave = wave_reg;

