module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);

    // State register and state encoding
    reg [1:0] state;
    parameter S0 = 2'b00, S1 = 2'b01, S2 = 2'b10, S3 = 2'b11;

    // Waveform register
    reg [4:0] wave_reg;

    // State control logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            state <= S0;
        end else begin
            case (state)
                S0: state <= S1;
                S1: state <= S2;
                S2: state <= S3;
                S3: state <= S0;
            endcase
        end
    end

    // Waveform generation logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave_reg <= 5'b00000;
        end else begin
            case (state)
                S0: wave_reg <= wave_reg + 1;
                S1: wave_reg <= wave_reg - 1;
                S2: wave_reg <= wave_reg;
                S3: wave_reg <= wave_reg;
            endcase
        end
    end

    // Output waveform
    assign wave = wave_reg;

endmodule

module stagepipe3(
    input clk,
    input rst,
    input [31:0] instr_mem [0:31],
    input [31:0] reg_file [0:31],
