    wire [31:0] result;

    fetch_stage fetch (
        .clk(clk),
        .rst(rst),
        .instr_mem(instr_mem),
        .pc(32'd0),
        .fetched_instr(fetched_instr)
    );

    execute execute (
        .clk(clk),
        .rst(rst),
        .instr(fetched_instr),
        .reg_file(reg_file),
        .result(result)
    );

    writeback writeback (
        .clk(clk),
        .rst(rst),
        .result(result),
        .reg_file(out_reg_file)
    );

endmodule

module fetch_stage(
    input clk,
    input rst,
    input [31:0] instr_mem [0:31],
    input [31:0] pc,
    output reg [31:0] fetched_instr
);

    always @(posedge clk or posedge rst) begin
        if (rst)
            fetched_instr <= 32'd0;
        else
            fetched_instr <= instr_mem[pc];
    end

endmodule

module execute(
    input clk,
    input rst,
    input [31:0] instr,
    input [31:0] reg_file [0:31],
    output reg [31:0] result
);

    wire [4:0] opcode;
    wire [4:0] rs1;
    wire [4:0] rs2;
    wire [4:0] rd;
    wire [31:0] imm;
    wire [31:0] op1;
    wire [31:0] op2;
    wire [31:0] sum;

    assign opcode = instr[31:28];
    assign rs1 = instr[27:22];
    assign rs2 = instr[21:16];
    assign rd = instr[15:11];
    assign imm = instr[31:20];

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            result <= 32'd0;
        end else begin
            case (opcode)
                5'b00000: result <= reg_file[rs1] + reg_file[rs2]; // Addition
                5'b00001: result <= reg_file[rs1] - reg_file[rs2]; // Subtraction
                default: result <= 32'd0;
            endcase
        end
    end

endmodule

module writeback(
    input clk,
    input rst,
    input [31:0] result,
    output reg [31:0] reg_file [0:31]
);

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            reg_file <= 32'd0;
        end else begin
            reg_file[rd] <= result;
        end
    end

endmodule

module sub_16bit(A, B, D, B_out);
