    output [15:0] D;
    output B_out;

    wire [3:0] B_out_sub1, B_out_sub2, B_out_sub3, B_out_sub4;

    // Instantiate the 4-bit full subtractor blocks
    sub_4bit sub1(A[3:0], B[3:0], D[3:0], B_out_sub1);
    sub_4bit sub2(A[7:4], B[7:4], D[7:4], B_out_sub2);
    sub_4bit sub3(A[11:8], B[11:8], D[11:8], B_out_sub3);
    sub_4bit sub4(A[15:12], B[15:12], D[15:12], B_out_sub4);

    // Output the final borrow-out
    assign B_out = B_out_sub1 | B_out_sub2 | B_out_sub3 | B_out_sub4;

endmodule

