    sub_2bit sub1(A[1:0], B[1:0], D[1:0], B_out_sub1);
    sub_2bit sub2(A[3:2], B[3:2], D[3:2], B_out_sub2);

    // Output the final borrow-out
    assign B_out = B_out_sub1 | B_out_sub2;

endmodule

module sub_2bit(A, B, D, B_out);
