module sub_1bit(A, B, D, B_out);
    input A, B;
    output D;
    output B_out;

    wire B_out_sub1;

    // Instantiate the 1-bit full subtractor block
    full_subtractor sub1(A, B, D, B_out_sub1);

    // Output the final borrow-out
    assign B_out = B_out_sub1;

