module sub_16bit(A, B, D, B_out);
    input [16:1] A, B;
    output [16:1] D;
    output B_out;

    wire [16:1] borrow;

    // Instantiate the 4-bit full subtractor blocks
    sub_4bit sub1(.A(A[16:4]), .B(B[16:4]), .D(D[16:4]), .borrow(borrow[16:12]));
    sub_4bit sub2(.A(A[12:8]), .B(B[12:8]), .D(D[12:8]), .borrow(borrow[12:8]));
    sub_4bit sub3(.A(A[8:4]), .B(B[8:4]), .D(D[8:4]), .borrow(borrow[8:4]));
    sub_4bit sub4(.A(A[4:1]), .B(B[4:1]), .D(D[4:1]), .borrow(borrow[4:1]));

    // Output the borrow-out
    assign B_out = borrow[1];

endmodule

module sub_4bit(A, B, D, borrow);
    input [4:1] A, B;
    output [4:1] D;
    output borrow;

    wire [4:1] diff;

    // Subtractor logic
    assign diff = A - B;

    // Borrow-out logic
    assign borrow = (A[4] & ~B[4]) | (A[4] & B[4] & ~diff[4]) | (~A[4] & B[4] & diff[4]);

    // Output the difference
    assign D = diff;
endmodule

module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);

    // State register and next state logic
    reg [1:0] state;
    reg [1:0] next_state;

    // Waveform register
    reg [4:0] waveform;

    // State control logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            state <= 2'b00;
        end else begin
            state <= next_state;
        end
    end

    // Next state logic
    always @(*) begin
        case (state)
            2'b00: next_state = 2'b01;
            2'b01: next_state = 2'b10;
            2'b10: next_state = 2'b11;
            2'b11: next_state = 2'b00;
            default: next_state = 2'b00;
        endcase
    end

    // Waveform generation logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            waveform <= 5'b00000;
        end else begin
            case (state)
                2'b00: waveform <= waveform + 1;
                2'b01: waveform <= waveform - 1;
                default: waveform <= waveform;
            endcase
        end
    end

    // Output waveform
    assign wave = waveform;

endmodule

