        if (rst) begin
            result <= 0;
        end else begin
            case (fetched_instr[31:26])
                6'b000000: result <= fetched_instr[5:0] + fetched_instr[31:6];
                6'b001000: result <= fetched_instr[5:0] - fetched_instr[31:6];
                default: result <= 0;
            endcase
        end
    end

