module fetch_stage(
    input clk,
    input rst,
    input [31:0] instr_mem [0:31],
    input [4:0] pc,
    output reg [31:0] fetched_instr
);

    always @(posedge clk or posedge rst) begin
        if (rst)
            fetched_instr <= 32'b0;
        else
            fetched_instr <= instr_mem[pc];
    end

endmodule

module execute(
    input clk,
    input rst,
    input [31:0] fetched_instr,
    output reg [31:0] op1,
    output reg [31:0] op2,
    output reg [31:0] result
);

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            op1 <= 32'b0;
            op2 <= 32'b0;
            result <= 32'b0;
        end else begin
            case (fetched_instr[31:26])
                6'b000000: begin // ADD
                    op1 <= fetched_instr[5:0];
                    op2 <= fetched_instr[31:26];
                    result <= op1 + op2;
                end
                6'b001010: begin // SUB
                    op1 <= fetched_instr[5:0];
                    op2 <= fetched_instr[31:26];
                    result <= op1 - op2;
                end
                default: result <= 32'b0;
            endcase
        end
    end

endmodule

