        .clk(clk),
        .rst_n(rst_n),
        .count_out(clk_div_int)
    );

    // Clock average generator submodule
    clk_average u_clk_average(
        .clk(clk),
        .clk_div_int(clk_div_int),
        .clk_div_int_adj(clk_div_int_adj)
    );

    // Clock adjustment submodule
    clk_adjustment u_clk_adjustment(
        .clk(clk),
        .clk_div_int_adj(clk_div_int_adj),
        .clk_div_int_adj_neg(clk_div_int_adj[MUL2_DIV_CLK-1:0])
    );

    // Logical OR of the two intermediate clock signals
    assign clk_div_int_adj_neg = ~clk_div_int_adj;
    assign clk_div = clk_div_int_adj_neg | clk_div_int_adj;

endmodule

