    input               rst_n,
    input               valid_in,
    output reg  [3:0]   count
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n)
            count <= 4'b0;
        else if (valid_in)
            count <= count + 1;
    end
endmodule

module data_accumulator (
    input               clk,
    input               rst_n,
    input       [7:0]   data_in,
    input               valid_in,
    input       [3:0]   count,
    output reg  [9:0]   accumulated_data
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n)
            accumulated_data <= 10'b0;
        else if (valid_in && count == 4)
            accumulated_data <= accumulated_data + data_in;
    end
endmodule

module valid_output (
    input               clk,
    input               rst_n,
    input       [3:0]   count,
    output reg         valid_out,
    output reg  [9:0]   data_out
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            valid_out <= 1'b0;
            data_out <= 10'b0;
        end else if (count == 4) begin
            valid_out <= 1'b1;
            data_out <= accumulated_data;
        end
    end
endmodule

module freq_divbyodd(
    input clk,
    input rst_n,
    output clk_div
);

    parameter NUM_DIV = 5;

    wire clk_div1, clk_div2;

    pos_edge_counter u_pos_counter (
        .clk(clk),
