        .clk_div(clk_div1)
    );

    neg_edge_counter u_neg_counter (
        .clk(clk),
        .rst_n(rst_n),
        .clk_div(clk_div2)
    );

    assign clk_div = clk_div1 | clk_div2;

endmodule

module pos_edge_counter(
    input clk,
    input rst_n,
    output reg clk_div
);

    reg [NUM_DIV-1:0] counter;

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            counter <= 0;
            clk_div <= 0;
        end else if (counter == NUM_DIV-1) begin
            counter <= 0;
            clk_div <= 1;
        end else begin
            counter <= counter + 1;
            clk_div <= 0;
