module adder_32bit(A,B,S,C32);
    input [32:1] A;
    input [32:1] B;
    output [32:1] S;
    output C32;

    wire [16:1] C1;
    wire [16:1] C2;

    // Instantiate the 16 bit CLA blocks
    carry_lookahead_adder CLA1 (.A(A[16:1]), .B(B[16:1]), .Cin(1'b0), .S(S[16:1]), .Cout(C1[1]));
    carry_lookahead_adder CLA2 (.A(A[32:17]), .B(B[32:17]), .Cin(C1[1]), .S(S[32:17]), .Cout(C2[1]));

    // Connect the carry outs
    assign C32 = C2[16];

endmodule

module carry_lookahead_adder(A,B,Cin,S,Cout);
    input [16:1] A;
    input [16:1] B;
    input Cin;
    output [16:1] S;
    output Cout;

    wire [16:1] C;

    // Full adder
    assign S = A ^ B ^ C;

    // Carry out logic
    assign Cout = (A & B) | (A & Cin) | (B & Cin);

endmodule

