module carry_lookahead_adder(A,B,S,C);
    input [16:1] A;
    input [16:1] B;
    output [16:1] S;
    output [16:1] C;

    wire [16:1] G;
    wire [16:1] P;

    // Generate the carry-out and sum
    assign C[16:1] = G[16:1];
    assign S[16:1] = A[16:1] ^ B[16:1] ^ G[16:1];

    // Generate the carry-in
    assign G[16:1] = {1'b0, A[16:1]} & {1'b0, B[16:1]};
    assign P[16:1] = {1'b0, A[16:1]} | {1'b0, B[16:1]};

endmodule

