module barrel_shifter (in, ctrl, out);

    input [7:0] in;
    input [2:0] ctrl;
    output [7:0] out;

    wire [7:0] shifted_out;
    wire [3:0] sel;

    assign sel = {ctrl[2], ctrl[1], ctrl[0]};

    // Shift by 4 positions
    mux2X1 mux2X1_4 (.sel(sel), .a(in), .b(shifted_out), .sel_out(shifted_out));
    // Shift by 2 positions
    mux2X1 mux2X1_2 (.sel(sel), .a(shifted_out), .b(in), .sel_out(shifted_out));
    // Shift by 1 position
    mux2X1 mux2X1_1 (.sel(sel), .a(shifted_out), .b(in), .sel_out(out));

endmodule

// Multiplexer module
module mux2X1 (input [3:0] sel, input [7:0] a, input [7:0] b, output reg [7:0] sel_out);
    always @(*) begin
        case (sel)
            3'b10000: sel_out = a;
            3'b0100: sel_out = b;
        endcase
    endmodule

module adder_32bit(A,B,S,C32);
    input [32:1] A, B;
    output [32:1] S;
    output C32;

    wire [16:1] C1, C2;

    // Instantiate the CLA blocks for each 16 bits
    CLA_16bit CLA1 (
        .A(A[16:1]),
        .B(B[16:1]),
        .Cin(1'b0),
        .S(S[16:1]),
        .Cout(C1[1])
    );

    CLA_16bit CLA2 (
        .A(A[32:17]),
        .B(B[32:17]),
        .Cin(C1[1]),
        .S(S[32:17]),
        .Cout(C2[1])
    );

    // Combine the carry outputs of the two CLA blocks
    assign C32 = C1[16] | C2[16];

