module CLA_16bit(A,B,Cin,S,Cout);
    input [16:1] A, B;
    input Cin;
    output [16:1] S;
    output Cout;

    wire [16:1] G, P;
    wire [16:1] C;

    // Full adder for each bit
    assign G = A ^ B;
    assign P = A & B;
    assign C[1] = Cin;

    // Generate the carry out
    assign Cout = (G[1] & P[1]) | (G[1] & C[1]) | (P[1] & C[1]);

    // Generate the sum
    assign S[1] = G[1] ^ C[1];

    // Generate the carry out for each bit
    genvar i;
    generate
        for (i = 2; i <= 16; i = i + 1) begin : carry_gen
            assign C[i] = (G[i] & P[i]) | (G[i] & C[i-1]) | (P[i] & C[i-1]);
            assign S[i] = G[i] ^ C[i-1];
        end
    endgenerate

endmodule

