module div_control (
    input wire [15:0] A,       // 16-bit dividend
    input wire [7:0] B,       // 8-bit divisor
    output reg [15:0] quotient, // 16-bit quotient
    output reg [15:0] remainder  // 16-bit remainder
);

    reg [15:0] dividend;
    reg [7:0] divisor;
    reg [3:0] shift_count;
    reg [15:0] temp_quotient;
    reg [15:0] temp_remainder;

    always @(*) begin
        dividend = A;
        divisor = B;
        shift_count = 0;
        quotient = 0;
        remainder = 0;
    end

    always @(posedge dividend[15]) begin
        if (dividend[15] == 1'b0) begin
            shift_count = shift_count + 1;
            dividend = {dividend[14:0], 1'b0};
        end
    end

    always @(posedge divisor[7]) begin
        if (divisor[7] == 1'b0) begin
            shift_count = shift_count - 1;
            divisor = {divisor[6:0], 1'b0};
        end
    end

    always @(posedge dividend[15] or posedge divisor[7]) begin
        if (dividend[15] == 1'b1 && divisor[7] == 1'b1) begin
            temp_quotient = dividend[15:8] - divisor;
            if (temp_quotient[7] == 1'b0) begin
                quotient[15:8] = temp_quotient;
                remainder[15:8] = dividend[7:0];
            end else begin
                temp_quotient = dividend[15:8] - divisor;
                quotient[15:8] = temp_quotient;
                remainder[15:8] = dividend[7:0];
            end
        end
    end

    always @(posedge dividend[15] or posedge divisor[7]) begin
        if (dividend[15] == 1'b1 && divisor[7] == 1'b1) begin
            temp_quotient = dividend[7:0] - divisor;
            if (temp_quotient[7] == 1'b0) begin
                quotient[7:0] = temp_quotient;
                remainder[7:0] = dividend[15:8];
            end else begin
                temp_quotient = dividend[7:0] - divisor;
                quotient[7:0] = temp_quotient;
                remainder[7:0] = dividend[15:8];
            end
        end
    end

