module div_ctrl (
    input wire [15:0] A,       // 16-bit dividend
    input wire [7:0] B,       // 8-bit divisor
    output reg [15:0] quotient, // 16-bit quotient
    output reg [15:0] remainder  // 16-bit remainder
);

    reg [15:0] dividend;
    reg [7:0] divisor;
    reg [3:0] shift_count;
    reg [15:0] temp_quotient;
    reg [15:0] temp_remainder;

    always @(*) begin
        dividend = A;
        divisor = B;
        shift_count = 0;
        temp_quotient = 0;
        temp_remainder = 0;
    end

    always @(posedge shift_count) begin
        if (shift_count == 16) begin
            quotient <= temp_quotient;
            remainder <= temp_remainder;
        end else begin
            dividend <= {dividend[14:0], 1'b0};
            divisor <= {divisor[6:0], 1'b0};
            shift_count <= shift_count + 1;
            if (dividend >= divisor) begin
                temp_quotient <= {temp_quotient[14:0], 1'b1};
                temp_remainder <= dividend - divisor;
            end else begin
                temp_quotient <= temp_quotient;
                temp_remainder <= dividend;
            end
        end
    end

endmodule

