    input wire [7:0] B,       // 8-bit divisor
    output wire [15:0] result, // 16-bit quotient
    output wire [15:0] odd     // 16-bit remainder
);

    wire [15:0] quotient;
    wire [15:0] remainder;

    // Instantiate the control unit
    control_unit ctrl (.A(A), .B(B), .quotient(quotient), .remainder(remainder));

    // Assign the outputs
    assign result = quotient;
    assign odd = remainder;

endmodule

module control_unit (
    input wire [15:0] A,       // 16-bit dividend
    input wire [7:0] B,       // 8-bit divisor
    output reg [15:0] quotient, // 16-bit quotient
    output reg [15:0] remainder  // 16-bit remainder
);

    // Initialize the quotient and remainder to 0
    initial begin
        quotient = 0;
        remainder = 0;
    end

    // Loop through the dividend bits
    integer i;
    always @* begin
        for (i = 0; i < 16; i = i + 1) begin
            if (A[i] == 1) begin
                quotient[i] = 1;
                remainder = remainder ^ (A << (16 - i));
            end
        end
    end

endmodule

