module control_unit (
    input wire [15:0] dividend,
    input wire [7:0] divisor,
    output reg [15:0] quotient,
    output reg [15:0] remainder
);

    reg [15:0] temp_dividend;
    reg [15:0] temp_divisor;
    reg [15:0] temp_quotient;
    reg [15:0] temp_remainder;
    reg [3:0] counter;
    reg [3:0] shift_count;

    initial begin
        temp_dividend = dividend;
        temp_divisor = divisor;
        temp_quotient = 0;
        temp_remainder = 0;
        counter = 0;
        shift_count = 0;
    end

    always @(*) begin
        if (counter == 0) begin
            temp_remainder = temp_dividend;
            temp_quotient = 0;
            shift_count = 0;
        end else begin
            temp_remainder = temp_dividend - temp_divisor;
            temp_quotient = {temp_quotient[14:0], 1'b0};
        end

        if (temp_remainder < temp_divisor) begin
            temp_quotient = {temp_quotient[14:0], 1'b1};
            temp_remainder = temp_remainder + temp_dividend;
        end

        if (counter == 15) begin
            quotient = temp_quotient;
            remainder = temp_remainder;
        end

        counter = counter + 1;
        shift_count = shift_count + 1;
        temp_dividend = {temp_dividend[14:0], 1'b0};
    end

endmodule

