module control_unit (
    input wire [15:0] A,       // 16-bit dividend
    input wire [7:0] B,       // 8-bit divisor
    output reg [15:0] quotient, // 16-bit quotient
    output reg [15:0] remainder // 16-bit remainder
);

    reg [15:0] temp_quotient;
    reg [15:0] temp_remainder;
    reg [15:0] temp_dividend;
    reg [3:0] counter;
    reg [2:0] shift_amount;
    reg [7:0] divisor;
    reg [15:0] dividend;
    reg [15:0] remainder_temp;

    always @(*) begin
        divisor = B;
        dividend = A;
        counter = 0;
        shift_amount = 0;
        remainder_temp = 0;
        quotient = 0;
        remainder = 0;
    end

    always @(posedge divisor or posedge dividend) begin
        if (counter < 16) begin
            temp_quotient[counter] = dividend[0];
            temp_remainder[counter] = dividend[0];
            dividend = {dividend[15:1], dividend[0]};
            counter = counter + 1;
        end else begin
            temp_quotient[counter] = dividend[0];
            temp_remainder[counter] = remainder_temp;
            remainder = temp_remainder;
            quotient = temp_quotient;
        end
    end

    always @(posedge divisor or posedge dividend) begin
        if (counter == 0) begin
            shift_amount = 0;
        end else begin
            shift_amount = counter - 1;
        end
    end

    always @(posedge divisor or posedge dividend) begin
        if (dividend[shift_amount] == divisor[7]) begin
            remainder_temp = dividend[shift_amount:0];
        end else begin
            remainder_temp = dividend[shift_amount:0] + divisor;
        end
    end

endmodule

module div_16bit (
    input wire [15:0] A,       // 16-bit dividend
    input wire [7:0] B,       // 8-bit divisor
    output wire [15:0] result, // 16-bit quotient
    output wire [15:0] odd     // 16-bit remainder
);

    wire [15:0] quotient;
    wire [15:0] remainder;

    // Instantiate the control unit
    div_control_unit ctrl (
        .dividend(A),
        .divisor(B),
        .quotient(quotient),
        .remainder(remainder)
    );

    // Assign the outputs
    assign result = quotient;
    assign odd = remainder;

endmodule

module div_control_unit (
    input wire [15:0] dividend,
    input wire [7:0] divisor,
    output reg [15:0] quotient,
    output reg [15:0] remainder
);

    integer i;
    reg [7:0] shifted_divisor;
    reg [15:0] shifted_dividend;
    reg [15:0] temp_quotient;
    reg [15:0] temp_remainder;

    always @(*) begin
        shifted_divisor = {1'b0, divisor};
        shifted_dividend = {1'b0, dividend};
        quotient = 16'b0;
        remainder = dividend;

        for (i = 0; i < 16; i = i + 1) begin
            if (shifted_dividend >= shifted_divisor) begin
                temp_quotient = {quotient[14:0], 1'b1};
                temp_remainder = shifted_dividend - shifted_divisor;
            end else begin
                temp_quotient = quotient;
                temp_remainder = shifted_dividend;
            end

            quotient = temp_quotient;
            remainder = temp_remainder;

            shifted_divisor = {shifted_divisor[6:0], 1'b0};
            shifted_dividend = {shifted_dividend[14:0], 1'b0};
        end
    end

endmodule

