module multi_16bit (
    input clk,          // Chip clock signal.
    input rst_n,        // Active-low reset signal.
    input start,        // Chip enable signal.
    input [15:0] ain,   // Input a (multiplicand) with a data width of 16 bits.
    input [15:0] bin,   // Input b (multiplier) with a data width of 16 bits.
    output [31:0] yout, // Product output with a data width of 32 bits.
    output done         // Chip output flag signal.
);

    wire [15:0] shifted_bin;
    wire [31:0] product;
    wire [15:0] shifted_ain;

    // Control Unit
    control_unit ctrl_unit (
        .clk(clk),
        .rst_n(rst_n),
        .start(start),
        .done(done)
    );

    // Register Unit
    register_unit reg_unit (
        .clk(clk),
        .rst_n(rst_n),
        .ain(ain),
        .bin(bin),
        .shifted_bin(shifted_bin),
        .shifted_ain(shifted_ain)
    );

    // Multiplier Unit
    multiplier_unit mul_unit (
        .clk(clk),
        .shifted_bin(shifted_bin),
        .shifted_ain(shifted_ain),
        .product(product)
    );

    // Output Assignment
    assign yout = product;

endmodule

module control_unit (
    input clk,
    input rst_n,
    input start,
    output reg done
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            done <= 0;
        end else if (start) begin
            done <= 0;
        end else begin
            done <= 1;
        end
    end

endmodule

module register_unit (
    input clk,
    input rst_n,
    input [15:0] ain,
    input [15:0] bin,
    output reg [15:0] shifted_bin,
    output reg [15:0] shifted_ain
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            shifted_bin <= 0;
            shifted_ain <= 0;
        end else begin
            shifted_bin <= {bin[14:0], 1'b0};
            shifted_ain <= {ain[14:0], 1'b0};
        end
    end

endmodule

module multiplier_unit (
    input clk,
    input [15:0] shifted_bin,
    input [15:0] shifted_ain,
    output reg [31:0] product
);

    always @(posedge clk) begin
        if (shifted_bin[0] == 1) begin
            product <= product + shifted_ain;
        end
    end

endmodule

module multi_16bit (
    input clk,          // Chip clock signal.
    input rst_n,        // Active-low reset signal.
    input start,        // Chip enable signal.
    input [15:0] ain,   // Input a (multiplicand) with a data width of 16 bits.
    input [15:0] bin,   // Input b (multiplier) with a data width of 16 bits.
    output [31:0] yout, // Product output with a data width of 32 bits.
    output done         // Chip output flag signal.
);

    wire [3:0] shift_count;
    wire [31:0] product;

    // Control Unit
    control_unit ctrl_unit (
        .clk(clk),
        .rst_n(rst_n),
        .start(start),
        .shift_count(shift_count),
        .done(done)
    );

    // Register Unit
    reg [15:0] a_reg, b_reg;
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            a_reg <= 0;
            b_reg <= 0;
        end else if (start) begin
            a_reg <= ain;
            b_reg <= bin;
        end
    end

    // Multiplier Unit
    multiplier_unit mul_unit (
        .clk(clk),
        .rst_n(rst_n),
        .shift_count(shift_count),
        .a_reg(a_reg),
        .b_reg(b_reg),
        .product(product)
    );

    // Output Assignment
    assign yout = product;

endmodule

// Control Unit
module control_unit (
    input clk,
    input rst_n,
    input start,
    output reg [3:0] shift_count,
    output reg done
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            shift_count <= 0;
            done <= 0;
        end else if (start) begin
            shift_count <= 1;
            done <= 0;
        end else if (shift_count == 16) begin
            shift_count <= 0;
            done <= 1;
        end else begin
            shift_count <= shift_count + 1;
            done <= 0;
        end
    end

endmodule

// Register Unit
module reg_unit (
    input clk,
    input rst_n,
    input [15:0] a_reg,
    input [15:0] b_reg,
    output reg [31:0] product
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            product <= 0;
        end else begin
            product <= {product[30:0], a_reg[shift_count]};
        end
    end

endmodule

// Multiplier Unit
module multiplier_unit (
    input clk,
    input rst_n,
    input [3:0] shift_count,
    input [15:0] a_reg,
    input [15:0] b_reg,
    output reg [31:0] product
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            product <= 0;
        end else begin
            if (shift_count == 0) begin
                product <= {product[30:0], b_reg[0]};
            end else begin
                product <= {product[30:0], b_reg[shift_count]};
            end
        end
    end

endmodule

