module pe(
    input clk,
    input rst,
    input [31:0] a,
    input [31:0] b,
    output [31:0] c
);
wire [63:0] mult_result;
wire [31:0] mult_result_lo;
u_multiplier u_multiplier(.a(a), .b(b), .mult_result(mult_result));
