module u_accumulator(
    input clk,
    input rst,
    input [31:0] mult_result_lo,
    output reg [31:0] c
);
always @(posedge clk) begin
    if (rst == 1) begin
        c <= 0;
    end
    else begin
        c <= c + mult_result_lo;
    end
end
endmodule

