module simple_alu(a, b, op, result, carry_borrow);
  input [3:0] a, b;
  input [1:0] op;
  output [3:0] result;
  output carry_borrow;
  wire [3:0] and_result, or_result, add_result, sub_result;
  bitwise_and and_unit(a, b, and_result);
  bitwise_or or_unit(a, b, or_result);
  adder_4bit add_unit(a, b, op[0], add_result, carry_borrow);
  subtractor_4bit sub_unit(a, b, op[0], sub_result, carry_borrow);
  mux_2to1 mux_result(and_result, or_result, add_result, sub_result, op[1], result);
endmodule
module bitwise_and(a, b, result);
  input [3:0] a, b;
  output [3:0] result;
  and and0(result[0], a[0], b[0]);
  and and1(result[1], a[1], b[1]);
  and and2(result[2], a[2], b[2]);
  and and3(result[3], a[3], b[3]);
endmodule
module bitwise_or(a, b, result);
  input [3:0] a, b;
  output [3:0] result;
  or or0(result[0], a[0], b[0]);
  or or1(result[1], a[1], b[1]);
  or or2(result[2], a[2], b[2]);
  or or3(result[3], a[3], b[3]);
endmodule
module adder_4bit(a, b, cin, result, cout);
  input [3:0] a, b;
  input cin;
  output [3:0] result;
  output cout;
  wire carry_out;
  full_adder fa0(a[0], b[0], cin, result[0], carry_out);
  full_adder fa1(a[1], b[1], carry_out, result[1], carry_out);
  full_adder fa2(a[2], b[2], carry_out, result[2], carry_out);
  full_adder fa3(a[3], b[3], carry_out, result[3], cout);
endmodule
module subtractor_4bit(a, b, cin, result, cout);
  input [3:0] a, b;
  input cin;
  output [3:0] result;
  output cout;
  wire carry_out;
  full_subtractor fs0(a[0], b[0], cin, result[0], carry_out);
  full_subtractor fs1(a[1], b[1], carry_out, result[1], carry_out);
  full_subtractor fs2(a[2], b[2], carry_out, result[2], carry_out);
  full_subtractor fs3(a[3], b[3], carry_out, result[3], cout);
endmodule
module full_adder(a, b, cin, sum, cout);
  input a, b, cin;
  output sum, cout;
  wire sum, cout;
  assign sum = a ^ b ^ cin;
  assign cout = (a & b) | (a & cin) | (b & cin);
endmodule
module full_subtractor(a, b, cin, difference, cout);
  input a, b, cin;
  output difference, cout;
  wire difference, cout;
  assign difference = a ^ b ^ cin;
  assign cout = (a & b) | (a & cin) | (b & cin);
endmodule
module mux_2to1(i0, i1, sel, out);
  input i0, i1, sel;
  output out;
  assign out = (sel == 0) ? i0 : i1;
endmodule

