module simple_alu(a, b, op, result, carry_borrow);
  input [3:0] a, b;
  input [1:0] op;
  output [3:0] result;
  output carry_borrow;
  wire [3:0] and_result, or_result, add_result, sub_result;
  bitwise_and and_unit(a, b, and_result);
  bitwise_or or_unit(a, b, or_result);
  adder_4bit add_unit(a, b, 1'b0, add_result, carry_add);
  subtractor_4bit sub_unit(a, b, 1'b0, sub_result, carry_sub);
  mux_4bit mux_unit(and_result, or_result, add_result, sub_result, op, result);
endmodule
module bitwise_and(a, b, result);
  input [3:0] a, b;
  output [3:0] result;
  and and0(result[0], a[0], b[0]);
  and and1(result[1], a[1], b[1]);
  and and2(result[2], a[2], b[2]);
  and and3(result[3], a[3], b[3]);
endmodule
module bitwise_or(a, b, result);
  input [3:0] a, b;
  output [3:0] result;
  or or0(result[0], a[0], b[0]);
  or or1(result[1], a[1], b[1]);
  or or2(result[2], a[2], b[2]);
  or or3(result[3], a[3], b[3]);
endmodule
module adder_4bit(a, b, cin, result, cout);
  input [3:0] a, b;
  input cin;
  output [3:0] result;
  output cout;
  wire carry_0, carry_1, carry_2;
  full_adder fa0(a[0], b[0], cin, result[0], carry_0);
  full_adder fa1(a[1], b[1], carry_0, result[1], carry_1);
  full_adder fa2(a[2], b[2], carry_1, result[2], carry_2);
  full_adder fa3(a[3], b[3], carry_2, result[3], cout);
endmodule
module full_adder(a, b, cin, sum, cout);
  input a, b, cin;
  output sum, cout;
  wire sum, cout;
  assign sum = a ^ b ^ cin;
  assign cout = (a & b) | (b & cin) | (cin & a);
endmodule
module subtractor_4bit(a, b, cin, result, cout);
  input [3:0] a, b;
  input cin;
  output [3:0] result;
  output cout;
  wire carry_0, carry_1, carry_2;
  subtractor_unit sub0(a[0], b[0], cin, result[0], carry_0);
  subtractor_unit sub1(a[1], b[1], carry_0, result[1], carry_1);
  subtractor_unit sub2(a[2], b[2], carry_1, result[2], carry_2);
  subtractor_unit sub3(a[3], b[3], carry_2, result[3], cout);
endmodule
module subtractor_unit(a, b, cin, result, cout);
  input a, b, cin;
  output result, cout;
  wire result, cout;
  assign result = a ^ b ^ cin;
  assign cout = (~a & b) | (b & cin) | (cin & ~a);
endmodule
module mux_4bit(and_result, or_result, add_result, sub_result, op, result);
  input [3:0] and_result, or_result, add_result, sub_result;
  input [1:0] op;
  output [3:0] result;
  reg [3:0] result;
  always @(and_result, or_result, add_result, sub_result, op)
    case (op)
      2'b00: result = and_result;
      2'b01: result = or_result;
      2'b10: result = add_result;
      2'b11: result = sub_result;
    endcase
endmodule

