    input CLK_in,
    input RST,
    output PWM_25,
    output PWM_50,
    output PWM_75
);
    // Instantiate the three PWM modules
    pwm_25 pwm_module_25 (CLK_in, RST, PWM_25);
    pwm_50 pwm_module_50 (CLK_in, RST, PWM_50);
    pwm_75 pwm_module_75 (CLK_in, RST, PWM_75);
endmodule
// PWM module with 25% duty cycle
module pwm_25 (
    input CLK_in,
    input RST,
    output reg PWM_out
);
    // 25% duty cycle threshold
    parameter THRESHOLD = 250000;
    // Running counter
    reg [18:0] counter;
    always @(posedge CLK_in or posedge RST) begin
        if (RST) begin
            // Reset the counter and PWM output
            counter <= 0;
            PWM_out <= 0;
        end else begin
            // Increment the counter on every positive edge of the clock
            counter <= counter + 1;
            // Compare the counter to the threshold to generate the PWM signal
            if (counter < THRESHOLD) begin
                PWM_out <= 1;
            end else begin
                PWM_out <= 0;
            end
        end
    end
endmodule
