// Submodule 1: Generates a 25% duty cycle PWM
module pwm_25 (
    input CLK_in,
    input RST,
    output reg PWM_out
);
    // Registers to hold the current and next thresholds
    reg [7:0] threshold_reg, threshold_next;
    // Counter to count up to 256
    reg [7:0] counter_reg, counter_next;
    // Sequential logic
    always @(posedge CLK_in, posedge RST) begin
        if (RST) begin
            threshold_reg <= 8'd192; // 25% duty cycle @ 256
            counter_reg <= 8'd0;
            PWM_out <= 1'b0;
        end else begin
            threshold_reg <= threshold_next;
            counter_reg <= counter_next;
            PWM_out <= (counter_reg < threshold_reg) ? 1'b1 : 1'b0;
        end
    end
    // Combinational logic
    always @(*) begin
        // Increment the counter on every positive edge of CLK_in
        counter_next = counter_reg + 1'b1;
        // Set the next threshold to 25% of the counter
        threshold_next = (counter_reg < 8'd192) ? counter_reg + 8'd32 : 8'd255;
    end
endmodule
