input [3:0] a, b;
 input [1:0] op;
 output [3:0] result;
 output carry_borrow;
 wire [3:0] and_result, or_result, add_result, sub_result;
 bitwise_and and_unit(.a(a), .b(b), .result(and_result));
 bitwise_or or_unit(.a(a), .b(b), .result(or_result));
 adder_4bit add_unit(.a(a), .b(b), .result(add_result), .carry_borrow(carry_borrow));
 subtractor_4bit sub_unit(.a(a), .b(b), .result(sub_result), .borrow_out(carry_borrow));
 mux_4bit mux_unit(.a(and_result), .b(or_result), .c(add_result), .d(sub_result), .op(op), .result(result));
endmodule
module bitwise_and(a, b, result); ASSISTANT: 
 input [3:0] a, b;
 output [3:0] result;
 and_4bit and_unit(.a(a), .b(b), .result(result));
endmodule
module bitwise_or(a, b, result); ASSISTANT: 
 input [3:0] a, b;
 output [3:0] result;
 or_4bit or_unit(.a(a), .b(b), .result(result));
endmodule
module adder_4bit(a, b, result, carry_borrow); ASSISTANT: 
 input [3:0] a, b;
 output [3:0] result;
 output carry_borrow;
 wire [3:0] carry;
 full_adder_1bit fa0(.a(a[0]), .b(b[0]), .carry_in(1'b0), .sum(result[0]), .carry_out(carry[0]));
 full_adder_1bit fa1(.a(a[1]), .b(b[1]), .carry_in(carry[0]), .sum(result[1]), .carry_out(carry[1]));
 full_adder_1bit fa2(.a(a[2]), .b(b[2]), .carry_in(carry[1]), .sum(result[2]), .carry_out(carry[2]));
 full_adder_1bit fa3(.a(a[3]), .b(b[3]), .carry_in(carry[2]), .sum(result[3]), .carry_out(carry_borrow));
endmodule
module subtractor_4bit(a, b, result, borrow_out); ASSISTANT: 
 input [3:0] a, b;
 output [3:0] result;
 output borrow_out;
 wire [3:0] carry;
 full_subtractor_1bit fs0(.a(a[0]), .b(b[0]), .carry_in(1'b0), .sum(result[0]), .carry_out(carry[0]));
 full_subtractor_1bit fs1(.a(a[1]), .b(b[1]), .carry_in(carry[0]), .sum(result[1]), .carry_out(carry[1]));
 full_subtractor_1bit fs2(.a(a[2]), .b(b[2]), .carry_in(carry[1]), .sum(result[2]), .carry_out(carry[2]));
 full_subtractor_1bit fs3(.a(a[3]), .b(b[3]), .carry_in(carry[2]), .sum(result[3]), .carry_out(borrow_out));
endmodule
module mux_4bit(a, b, c, d, op, result); ASSISTANT: 
 input [3:0] a, b, c, d;
 input [1:0] op;
 output [3:0] result;
 reg [3:0] result_reg;
 always @(a or b or c or d or op) begin
    case(op)
        2'b00: result_reg = a;
        2'b01: result_reg = b;
        2'b10: result_reg = c;
        2'b11: result_reg = d;
    endcase
 end
 assign result = result_reg;
endmodule
module and_4bit(a, b, result); ASSISTANT: 
 input [3:0] a, b;
 output [3:0] result;
 and and0(.a(a[0]), .b(b[0]), .out(result[0]));
 and and1(.a(a[1]), .b(b[1]), .out(result[1]));
 and and2(.a(a[2]), .b(b[2]), .out(result[2]));
 and and3(.a(a[3]), .b(b[3]), .out(result[3]));
endmodule
module or_4bit(a, b, result); ASSISTANT: 
 input [3:0] a, b;
 output [3:0] result;
 or or0(.a(a[0]), .b(b[0]), .out(result[0]));
 or or1(.a(a[1]), .b(b[1]), .out(result[1]));
 or or2(.a(a[2]), .b(b[2]), .out(result[2]));
 or or3(.a(a[3]), .b(b[3]), .out(result[3]));
endmodule
module full_adder_1bit(a, b, carry_in, sum, carry_out); ASSISTANT: 
 input a, b, carry_in;
 output sum, carry_out;
 wire sum_wire, carry_wire, carry_wire2;
 half_adder_1bit ha0(.a(a), .b(b), .sum(sum_wire), .carry_out(carry_wire));
 half_adder_1bit ha1(.a(carry_in), .b(sum_wire), .sum(sum), .carry_out(carry_wire2));
 or or0(.a(carry_wire), .b(carry_wire2), .out(carry_out));
endmodule
module half_adder_1bit(a, b, sum, carry_out); ASSISTANT: 
 input a, b;
 output sum, carry_out;
 xor xor0(.a(a), .b(b), .out(sum));
 and and0(.a(a), .b(b), .out(carry_out));
endmodule
module full_subtractor_1bit(a, b, carry_in, sum, carry_out); ASSISTANT: 
 input a, b, carry_in;
 output sum, carry_out;
 wire sum_wire, carry_wire, carry_wire2;
 half_subtractor_1bit hs0(.a(a), .b(b), .sum(sum_wire), .carry_out(carry_wire));
 half_subtractor_1bit hs1(.a(carry_in), .b(sum_wire), .sum(sum), .carry_out(carry_wire2));
 or or0(.a(carry_wire), .b(carry_wire2), .out(carry_out));
endmodule
module half_subtractor_1bit(a, b, sum, carry_out); ASSISTANT: 
 input a, b;
 output sum, carry_out;
 wire sum_wire, carry_wire;
 xor xor0(.a(a), .b(b), .out(sum_wire));
 and and0(.a(a), .b(b), .out(carry_wire));
 xor xor1(.a(sum_wire), .b(carry_wire), .out(sum));
 and and1(.a(sum_wire), .b(carry_wire), .out(carry_out));
endmodule

