module pe(
    input clk,
    input rst,
    input [31:0] a,
    input [31:0] b,
    output [31:0] c
);
wire [63:0] mult_result;
wire [31:0] mult_result_lo;
wire [31:0] accumulate_result;
u_multiplier u_multiplier(
    .a(a),
    .b(b),
    .mult_result(mult_result)
);
u_accumulator u_accumulator(
    .clk(clk),
    .rst(rst),
    .mult_result_lo(mult_result_lo),
    .accumulate_result(accumulate_result)
);
assign c = accumulate_result;
endmodule
module u_multiplier(
    input [31:0] a,
    input [31:0] b,
    output [63:0] mult_result
);
assign mult_result = a * b;
endmodule
module u_accumulator(
    input clk,
    input rst,
    input [31:0] mult_result_lo,
    output [31:0] accumulate_result
);
reg [31:0] current_value;
always @(posedge clk) begin
    if (rst == 1) begin
        current_value <= 0;
    end else begin
        current_value <= current_value + mult_result_lo;
    end
end
assign accumulate_result = current_value;
endmodule

