module simple_alu(
    input clk,
    input rst,
    input [1:0] op_code,
    input [31:0] operand_a,
    input [31:0] operand_b,
    output reg [31:0] result
);
    wire [31:0] add_result;
    wire [31:0] subtract_result;
    wire [31:0] bitwise_and_result;
    wire [31:0] bitwise_or_result;
    add add(
        .clk(clk),
        .rst(rst),
        .a(operand_a),
        .b(operand_b),
        .result(add_result)
    );
    subtract subtract(
        .clk(clk),
        .rst(rst),
        .a(operand_a),
        .b(operand_b),
        .result(subtract_result)
    );
    bitwise_and bitwise_and(
        .clk(clk),
        .rst(rst),
        .a(operand_a),
        .b(operand_b),
        .result(bitwise_and_result)
    );
    bitwise_or bitwise_or(
        .clk(clk),
        .rst(rst),
        .a(operand_a),
        .b(operand_b),
        .result(bitwise_or_result)
    );
    always @(posedge clk) begin
        if (rst) begin
            result <= 0;
        end else begin
            case (op_code)
                2'b00: result <= add_result;
                2'b01: result <= subtract_result;
                2'b10: result <= bitwise_and_result;
                2'b11: result <= bitwise_or_result;
            endcase
        end
    end
endmodule
