module as_module(
    input clk,
    input rst,
    input mode,
    input [31:0] op1,
    input [31:0] op2,
    output [31:0] result
);
    wire [31:0] adder_result, subtractor_result;
    u_adder u_adder(
        .op1(op1),
        .op2(op2),
        .result(adder_result)
    );
    u_subtractor u_subtractor(
        .op1(op1),
        .op2(op2),
        .result(subtractor_result)
    );
    assign result = mode ? subtractor_result : adder_result;
endmodule
module u_adder(
    input [31:0] op1,
    input [31:0] op2,
    output [31:0] result
);
    assign result = op1 + op2;
endmodule
module u_subtractor(
    input [31:0] op1,
    input [31:0] op2,
    output [31:0] result
);
    assign result = op1 - op2;
endmodule

