//add_16bit.v
//R. T. Russell, 2004
//
//Module for 16-bit carry-lookahead adder
module cla_16bit(A, B, S, C_out);
input [16:1] A, B;
output [16:1] S;
output C_out;
wire [16:1] carry;
genvar i;
generate
for(i=1; i<=16; i=i+1)
begin: add
cla_4bit cla_4bit(.A(A[i:i-3]), .B(B[i:i-3]), .carry_in(carry[i-1]), .S(S[i:i-3]), .carry_out(carry[i]));
end
endgenerate
assign C_out = carry[16];
endmodule
module cla_4bit(A, B, carry_in, S, carry_out);
input [4:1] A, B;
input carry_in;
output [4:1] S;
output carry_out;
wire [4:1] carry;
full_adder fa1(.A(A[1]), .B(B[1]), .carry_in(carry_in), .S(S[1]), .carry_out(carry[1]));
full_adder fa2(.A(A[2]), .B(B[2]), .carry_in(carry[1]), .S(S[2]), .carry_out(carry[2]));
full_adder fa3(.A(A[3]), .B(B[3]), .carry_in(carry[2]), .S(S[3]), .carry_out(carry[3]));
full_adder fa4(.A(A[4]), .B(B[4]), .carry_in(carry[3]), .S(S[4]), .carry_out(carry[4]));
assign carry_out = carry[4];
endmodule
module full_adder(A, B, carry_in, S, carry_out);
input A, B, carry_in;
output S, carry_out;
assign S = A ^ B ^ carry_in;
assign carry_out = (A & B) | (carry_in & (A ^ B));
endmodule

module comparator_16bit(a, b, eq, gt, lt);
  input [15:0] a, b;
  output eq, gt, lt;
  wire [3:0] eq_slice, gt_slice, lt_slice;
  wire [3:0] eq_slice1, eq_slice2, eq_slice3, eq_slice4;
  wire [3:0] gt_slice1, gt_slice2, gt_slice3, gt_slice4;
  wire [3:0] lt_slice1, lt_slice2, lt_slice3, lt_slice4;
  comp_slice comp_slice1(.a(a[3:0]), .b(b[3:0]), .eq(eq_slice1), .gt(gt_slice1), .lt(lt_slice1));
  comp_slice comp_slice2(.a(a[7:4]), .b(b[7:4]), .eq(eq_slice2), .gt(gt_slice2), .lt(lt_slice2));
  comp_slice comp_slice3(.a(a[11:8]), .b(b[11:8]), .eq(eq_slice3), .gt(gt_slice3), .lt(lt_slice3));
  comp_slice comp_slice4(.a(a[15:12]), .b(b[15:12]), .eq(eq_slice4), .gt(gt_slice4), .lt(lt_slice4));
  assign eq_slice[0] = eq_slice1[0] & eq_slice2[0] & eq_slice3[0] & eq_slice4[0];
  assign eq_slice[1] = eq_slice1[1] & eq_slice2[1] & eq_slice3[1] & eq_slice4[1];
  assign eq_slice[2] = eq_slice1[2] & eq_slice2[2] & eq_slice3[2] & eq_slice4[2];
  assign eq_slice[3] = eq_slice1[3] & eq_slice2[3] & eq_slice3[3] & eq_slice4[3];
  assign gt_slice[0] = gt_slice1[0] | (eq_slice1[0] & gt_slice2[0]);
  assign gt_slice[1] = gt_slice1[1] | (eq_slice1[1] & gt_slice2[1]);
  assign gt_slice[2] = gt_slice1[2] | (eq_slice1[2] & gt_slice2[2]);
  assign gt_slice[3] = gt_slice1[3] | (eq_slice1[3] & gt_slice2[3]);
  assign lt_slice[0] = lt_slice1[0] | (eq_slice1[0] & lt_slice2[0]);
  assign lt_slice[1] = lt_slice1[1] | (eq_slice1[1] & lt_slice2[1]);
  assign lt_slice[2] = lt_slice1[2] | (eq_slice1[2] & lt_slice2[2]);
  assign lt_slice[3] = lt_slice1[3] | (eq_slice1[3] & lt_slice2[3]);
  assign eq = eq_slice[0] & eq_slice[1] & eq_slice[2] & eq_slice[3];
  assign gt = gt_slice[0] | (eq_slice[0] & gt_slice[1]) | (eq_slice[0] & eq_slice[1] & gt_slice[2]) | (eq_slice[0] & eq_slice[1] & eq_slice[2] & gt_slice[3]);
  assign lt = lt_slice[0] | (eq_slice[0] & lt_slice[1]) | (eq_slice[0] & eq_slice[1] & lt_slice[2]) | (eq_slice[0] & eq_slice[1] & eq_slice[2] & lt_slice[3]);
endmodule
module comp_slice(a, b, eq, gt, lt);
  input [3:0] a, b;
  output eq, gt, lt;
  assign eq = (a == b);
  assign gt = (a > b);
  assign lt = (a < b);
endmodule

// This is the top module of the subtractor
`timescale 1ns/1ps
module subtractor_32bit(A, B, Bin, D, Bout);
    input [32:1] A, B;
    input Bin;
    output [32:1] D;
    output Bout;
    wire [8:1] B1, B2, B3, B4, B5, B6, B7;
    subtractor_8bit SUB1(A[8:1], B[8:1], Bin, D[8:1], B1[1]);
    subtractor_8bit SUB2(A[16:9], B[16:9], B1[1], D[16:9], B2[1]);
    subtractor_8bit SUB3(A[24:17], B[24:17], B2[1], D[24:17], B3[1]);
    subtractor_8bit SUB4(A[32:25], B[32:25], B3[1], D[32:25], B4[1]);
    subtractor_8bit SUB5(A[24:17], B[24:17], 1'b1, B5[8:1], B5[1]);
    subtractor_8bit SUB6(A[16:9], B[16:9], B5[1], B5[8:1], B6[1]);
    subtractor_8bit SUB7(A[8:1], B[8:1], B6[1], B5[8:1], B7[1]);
    subtractor_8bit SUB8(A[32:25], B[32:25], B7[1], B4[8:1], Bout);
endmodule
// 8-bit subtractor block
module subtractor_8bit(A, B, Bin, D, Bout);
    input [8:1] A, B;
    input Bin;
    output [8:1] D;
    output Bout;
    wire [7:1] B1, B2, B3, B4, B5, B6, B7;
    full_adder_1bit FADD1(A[1], B[1], Bin, D[1], B1);
    full_adder_1bit FADD2(A[2], B[2], B1, D[2], B2);
    full_adder_1bit FADD3(A[3], B[3], B2, D[3], B3);
    full_adder_1bit FADD4(A[4], B[4], B3, D[4], B4);
    full_adder_1bit FADD5(A[5], B[5], B4, D[5], B5);
    full_adder_1bit FADD6(A[6], B[6], B5, D[6], B6);
    full_adder_1bit FADD7(A[7], B[7], B6, D[7], Bout);
endmodule
// Full adder for 1 bit
module full_adder_1bit(A, B, Cin, Sum, Cout);
    input A, B, Cin;
    output Sum, Cout;
    assign Sum = A ^ B ^ Cin;
    assign Cout = (A & B) | (A & Cin) | (B & Cin);
endmodule

// This is a 32-bit binary subtractor
`timescale 1ns/1ps
module subtractor_32bit(A, B, Bin, D, Bout);
    input [32:1] A;
    input [32:1] B;
    input Bin;
    output [32:1] D;
    output Bout;
    wire [8:1] bout;
    subtractor_8bit SUB1(A[8:1], B[8:1], Bin, D[8:1], bout[1]);
    subtractor_8bit SUB2(A[16:9], B[16:9], bout[1], D[16:9], bout[2]);
    subtractor_8bit SUB3(A[24:17], B[24:17], bout[2], D[24:17], bout[3]);
    subtractor_8bit SUB4(A[32:25], B[32:25], bout[3], D[32:25], Bout);
endmodule
module subtractor_8bit(A, B, Bin, D, Bout);
    input [8:1] A;
    input [8:1] B;
    input Bin;
    output [8:1] D;
    output Bout;
    wire [7:1] bout;
    subtractor_block SUB1(A[1], B[1], Bin, D[1], bout[1]);
    subtractor_block SUB2(A[2], B[2], bout[1], D[2], bout[2]);
    subtractor_block SUB3(A[3], B[3], bout[2], D[3], bout[3]);
    subtractor_block SUB4(A[4], B[4], bout[3], D[4], bout[4]);
    subtractor_block SUB5(A[5], B[5], bout[4], D[5], bout[5]);
    subtractor_block SUB6(A[6], B[6], bout[5], D[6], bout[6]);
    subtractor_block SUB7(A[7], B[7], bout[6], D[7], bout[7]);
    subtractor_block SUB8(A[8], B[8], bout[7], D[8], Bout);
endmodule
module subtractor_block(A, B, Bin, D, Bout);
    input A;
    input B;
    input Bin;
    output D;
    output Bout;
    wire w1, w2, w3;
    xor(w1, A, B);
    xor(D, Bin, w1);
    and(w2, A, B);
    and(w3, Bin, w1);
    or(Bout, w2, w3);
endmodule

