module add_16bit(A, B, S, C_out);
input [16:1] A;
input [16:1] B;
output [16:1] S;
output C_out;
wire [16:1] carry;
wire [16:1] sum;
four_bit_add fba1(.A(A[4:1]), .B(B[4:1]), .C_in(carry[4]), .S(sum[4:1]), .C_out(carry[4]));
four_bit_add fba2(.A(A[8:5]), .B(B[8:5]), .C_in(carry[8]), .S(sum[8:5]), .C_out(carry[8]));
four_bit_add fba3(.A(A[12:9]), .B(B[12:9]), .C_in(carry[12]), .S(sum[12:9]), .C_out(carry[12]));
four_bit_add fba4(.A(A[16:13]), .B(B[16:13]), .C_in(carry[16]), .S(sum[16:13]), .C_out(carry[16]));
assign S = sum;
assign C_out = carry[16];
endmodule
module four_bit_add(A, B, C_in, S, C_out);
input [4:1] A;
input [4:1] B;
input C_in;
output [4:1] S;
output C_out;
wire [4:1] carry;
add1 a1(.A(A[1]), .B(B[1]), .C_in(carry[1]), .S(S[1]), .C_out(carry[1]));
add1 a2(.A(A[2]), .B(B[2]), .C_in(carry[2]), .S(S[2]), .C_out(carry[2]));
add1 a3(.A(A[3]), .B(B[3]), .C_in(carry[3]), .S(S[3]), .C_out(carry[3]));
add1 a4(.A(A[4]), .B(B[4]), .C_in(C_in), .S(S[4]), .C_out(C_out));
endmodule
module add1(A, B, C_in, S, C_out);
input A;
input B;
input C_in;
output S;
output C_out;
assign S = A ^ B ^ C_in;
assign C_out = (A & B) | (A & C_in) | (B & C_in);
endmodule

