wire [16:1] C;
wire [16:1] P;
wire [16:1] G;
wire [16:1] A_in;
wire [16:1] B_in;
assign A_in = A;
assign B_in = B;
cla_4bit cla1(.A(A_in[4:1]), .B(B_in[4:1]), .Cin(C[1]), .S(S[4:1]), .Cout(C[2]));
cla_4bit cla2(.A(A_in[8:5]), .B(B_in[8:5]), .Cin(C[5]), .S(S[8:5]), .Cout(C[6]));
cla_4bit cla3(.A(A_in[12:9]), .B(B_in[12:9]), .Cin(C[9]), .S(S[12:9]), .Cout(C[10]));
